const accordionData = [
    {
        title: 'Can we register for more than one course',
        content: `Yes, you can register for more than one course.`
    },
    {
        title: 'Are courses accessible to learners with disabilities?',
        content: `Technology expert strives to create an innovative online-learning platform that promotes accessibility for all learners, regardless of any physical limitation that they might have. edX is dedicated to creating a platform that is not 
        only itself accessible, but also enables course content creators to create accessible content.`
    },
    {
        title: 'How do I redeem an enrollment code?',
        content: `Browse or search for the course you want to take. 
Select Enroll Now on the course enrollment page. A message displays confirming your enrollment. 
Select Pursue a Verified Certificate. A payment page appears.
In the coupon box, enter the enrollment code and select Apply.
Your cart will show $0 and you can now proceed to the course.`
    },
    {
        title: 'Are there quizzes in the course?',
        content: `No, there are no quizzes in the course'.
`
    },
    {
        title: 'Can I earn a certificate after the end?',
        content: `No, it is not possible to earn a certificate in a course session that has ended.`
    }
];

export default accordionData;
