import './Home.css';

import htmlCourse from "./HtmlCourse";
import blockchain from "./Blockchain";
import AngularCourse from "./Angular";
import MachineLearning from "./MachineLearning";

function Home({onNav}) {

    const htmllink = htmlCourse.map(item => {
        return (
            <a className="readmore" onClick={onNav} key={item.title} href="{item.url}" data-target={item.title}>Read
                More </a>

        );
    });
    const blockchainlink = blockchain.map(item => {
        return (
            <a className="readmore" onClick={onNav} key={item.title} href="{item.url}" data-target={item.title}>Read
                More </a>

        );
    });
    const angularlink = AngularCourse.map(item => {
        return (
            <a className="readmore" onClick={onNav} key={item.title} href="{item.url}" data-target={item.title}>Read
                More </a>

        );
    });

    const MachineLearninglink = MachineLearning.map(item => {
        return (
            <a className="readmore" onClick={onNav} key={item.title} href="{item.url}" data-target={item.title}>Read
                More </a>

        );
    });

    return (
        <div className="card-content">
            <div className="content-heading">
                <h2>Courses Avaliable</h2>
            </div>
            <div className="card1">
                <h3>HTML</h3>
                <div className="image"><img alt="Cat from Chinese breed" src={require("./images/html.jpg")}/>
                </div>
                <p>HTML course provides training on basic and advanced concepts of HTML. Our HTML tutorial is developed for
                    beginners and professionals. In our tutorial, every topic is given step-by-step so that you can
                    learn it in a very easy way. If you are new in learning HTML, then you can learn HTML from basic to a
                    professional level and after learning HTML with CSS and JavaScript you will be
                    able to create your own interactive and dynamic website.
                </p>
                <div className="moreinfo">{htmllink}</div>
            </div>
            <div className="card2">
                <h3>Blockchain</h3>
                <div className="image"><img alt="Cat from Bombay breed" src={require("./images/blockchain.jpg")}/>
                </div>
                <p>Blockchain is a shared, immutable ledger that facilitates the process of recording transactions
                    and tracking assets in a business network. An asset can be tangible (a house, car, cash, land) or
                    intangible intellectual property, patents, copyrights, branding.
                    Virtually anything of value can be tracked and traded on a blockchain network, reducing risk
                    and cutting costs for all involved.</p>
                <div className="moreinfo">{blockchainlink}</div>
            </div>
            <div className="card3">
                <h3>Angular</h3>
                <div className="image"><img alt="Cat from Bengal breed" src={require("./images/angular.jpg")}/>
                </div>
                <p>Angular JS is an open-source, client-side TypeScript based JavaScript framework.
                    It is written in TypeScript and complied into JavaScript. Angular JS is used to create dynamic web applications.
                    It is very similar to its previous versions except having some extensive features.Angular community has released
                    its latest version known as Angular 11.
                </p>
                <div className="moreinfo">{angularlink}</div>
            </div>
            <div className="card4">
                <h3>Machine Learning</h3>
                <div className="image"><img alt="Cat from Random breed" src={require("./images/machine-learning.jpg")}/>
                </div>
                <p>Machine Learning course provides basic and advanced concepts of machine learning.
                    Our machine learning tutorial is designed for students and working professionals.
                    Machine learning is a growing technology which enables computers to learn automatically
                    from past data. Machine learning uses various algorithms for
                    building mathematical models and making predictions using historical data or information.
                </p>
                <div className="moreinfo">{MachineLearninglink}</div>
            </div>
        </div>
    );

}

export default Home;
