import './contactus.css';
import {useState} from "react";

function ContactUs() {
    const [contactFormData, setContactFormData] = useState({
        email: '',
        text: '',
    })

    const [contactFormError, setContactFormError] = useState({})

    const onChangeHandler = (event) => {
        setContactFormData(() => ({
            ...contactFormData,
            [event.target.name]: event.target.value
        }))
    }

    const validateForm = () => {
        let error = {}

        if (contactFormData.email === '') {
            error.email = 'Email Id require'
        }

        if (contactFormData.text === '') {
            error.text = 'Please mention your query'
        }
    }

    const onSubmitHandler = (event) => {
        event.preventDefault()
        let isValid = validateForm()

        if (isValid) {

            alert('Your query send');
            setFormData(() => ({
                email: '',
                text: '',
            }))
        }
        else
        {
            alert('Invalid Form');
        }

    }



    return(
        <div>
        <h2>We'd Love to Hear from You</h2>
        <h3>For any enquires, fill out the form below and a team member will get back to you.</h3>


        <form onSubmit={onSubmitHandler} className="registration-form">
            <p className="contactForm-group">
                <label htmlFor="email" className="form-label ">Email Id:
                    <span className="required"><strong>*</strong>required</span>
                </label>
                <input className="form-control " name="email" onChange={onChangeHandler} value={contactFormData.email} />
                <span className='non-valid'>{contactFormError.email}</span>
            </p>

<p className="contactForm-group">
    <label htmlFor="text" className="form-label ">Your message:
        <span className="required"><strong>*</strong>required</span>
    </label>
    <textarea className="form-control " name="text" onChange={onChangeHandler} value={contactFormData.text} />
    <span className='non-valid'>{contactFormError.text}</span>
</p>


            <button type="submit-button">Send</button>
        </form>
    </div>);



};

export default ContactUs;
