import "./Register.css";
import {useState} from "react";

function Register({onNav}){
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        institute: '',
        occupation: '',
        company: '',
        gender: '',
        languages: [],
    })

    const [formError, setFormError] = useState({})
    const [institute, setInstitute] = useState();
    const [company, setCompany] = useState();

    const onChangeHandler = (event) => {
debugger
        console.log(event)
        if (event.target.name === 'languages') {

            let copy = { ...formData }

            if (event.target.checked) {
                copy.languages.push(event.target.value)
            } else {
                copy.languages = copy.languages.filter(el => el !== event.target.value)
            }

            setFormData(copy)

        } else {
            setFormData(() => ({
                ...formData,
                [event.target.name]: event.target.value
            }))
        }

        if(event.target.value==='')
        {

            const getinstitute= event.target.value;
            setInstitute('');
            setCompany('');
            // const instituteName= institute? 'form-group':'form-group institute';
        }

        if(formData.institute)
        {
            setInstitute('student');
            setCompany('');
        }

        if(formData.company)
        {
            setInstitute('');
            setCompany('employee');
        }

        if(event.target.value==='student')
        {
            debugger
            const getinstitute= event.target.value;
            setInstitute(getinstitute);
            setCompany('');
            // const instituteName= institute? 'form-group':'form-group institute';
        }

        if(event.target.value==='employee')
        {
            debugger
            const getcompany= event.target.value;
            setCompany(getcompany);
            setInstitute('');
            // const instituteName= institute? 'form-group':'form-group institute';
        }
    }

    const validateForm = () => {
        let err = {}

        if (formData.username === '') {
            err.username = 'Username required!'
        }
        if (formData.email === '') {
            err.email = 'Email required!'
        } else {
            let regex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/
            if (!regex.test(formData.email)) {
                err.email = 'Email not valid!'
            }
        }

        if (formData.occupation === '') {
            err.occupation = 'Occupation required!'
        }

        if (formData.gender === '') {
            err.gender = 'Gender required!'
        }
        if (formData.languages.length < 1) {
            err.languages = 'At least one language required!'
        }

        setFormError({ ...err })

        return Object.keys(err).length < 1;
    }

    const onSubmitHandler = (event) => {
        event.preventDefault()
        console.log("Form Data:", formData)
        let isValid = validateForm()

        if (isValid) {
            debugger
            alert('Submitted')
            setFormData(() => ({
                username: '',
                email: '',
                institute: '',
                occupation: '',
                gender: '',
                languages: [],
            }))
            setCompany('');
            setInstitute('');
            //API call to server
        } else {
            alert('InValid Form')
        }
        console.log(isValid)
    }
    return (
        <div className="main-registration-form">
            <h2>Registration Form</h2>
            <form className="registration-form"  onSubmit={onSubmitHandler}>
                {/*<div className="form-group" id="register">*/}
                    <label htmlFor="username" className="form-label">User Name:
                        <span className="required"><strong>*</strong>required</span>
                    </label>
                    <input className="form-control" name="username" onChange={onChangeHandler} value={formData.username} />
                    <span className='non-valid'>{formError.username}</span>
                {/*</div>*/}
                {/*<div className="form-group">*/}
                    <label htmlFor="email" className="form-label email-label">Email
                        <span className="required"><strong>*</strong>required</span>
                    </label>
                    <input className="form-control email-input" name="email" onChange={onChangeHandler} value={formData.email} />
                    <span className='non-valid'>{formError.email}</span>
                {/*</div>*/}

                {/*<div className="form-group">*/}
                    <label htmlFor="occupation" className="form-label">Occupation
                        <span className="required"><strong>*</strong>required</span>
                    </label>
                    <select className="form-select" name="occupation" onChange={onChangeHandler} value={formData.occupation}>
                        <option value=""></option>
                        <option value="student">Student</option>
                        <option value="employee">Employee</option>
                    </select>
                    <span className='non-valid'>{formError.occupation}</span>
                {/*</div>*/}
                {
                    institute ==='student'  &&
                    (
                        <div className="form-group">
                            <label htmlFor="institute" className="form-label">Institute</label>
                            <input className="form-control" name="institute" onChange={onChangeHandler} value={formData.institute} />
                            <span className='non-valid'>{formError.institute}</span>
                        </div>

                    )
                }

                {
                    company ==='employee' &&
                    (
                        <div className="form-group">
                            <label htmlFor="company" className="form-label">Company</label>
                            <input className="form-control" name="company" onChange={onChangeHandler} value={formData.company} />
                            <span className='non-valid'>{formError.company}</span>
                        </div>

                    )
                }

                {/*<div className="form-group">*/}
                    <label htmlFor="gender" className="form-label">Gender</label>
                    <div>
                        <div>
                            <input type="radio" name="gender" value="male" onChange={onChangeHandler} checked={formData.gender === 'male'} />
                            <label htmlFor="male">Male</label>
                        </div>
                        <div>
                            <input type="radio" name="gender" value="female" onChange={onChangeHandler} checked={formData.gender === 'female'} />
                            <label htmlFor="female">Female</label>
                        </div>
                        <div>
                            <input type="radio" name="gender" value="other" onChange={onChangeHandler} checked={formData.gender === 'other'} />
                            <label htmlFor="other">Other</label>
                        </div>
                        <span className='non-valid'>{formError.gender}</span>
                    </div>
                {/*</div>*/}
                {/*<div className="form-group">*/}
                    <label htmlFor="languages" className="form-label">Languages Known</label>
                    <div>
                        <div>
                            <input type="checkbox" name="languages" value="java" onChange={onChangeHandler} checked={formData.languages.indexOf('java') !== -1} />
                            <label htmlFor="java">Java</label>
                        </div>
                        <div>
                            <input type="checkbox" name="languages" value="swift" onChange={onChangeHandler} checked={formData.languages.indexOf('swift') !== -1} />
                            <label htmlFor="swift">Swift</label>
                        </div>
                        <div>
                            <input type="checkbox" name="languages" value="python" onChange={onChangeHandler} checked={formData.languages.indexOf('python') !== -1} />
                            <label htmlFor="python">Python</label>
                        </div>
                    </div>
                    <span className='non-valid'>{formError.languages}</span>
                {/*</div>*/}
                <div className="form-group">
                    <button className="btn" type="submit" >Submit</button>
                </div>
            </form>
        </div>
    );
};

export default Register;
